mapper.select:
select count(*) 
from board 
where board_type=#{board_type} and is_privated  <=  #{is_privated}

select count(*) 
from board 
where board_type=#{board_type}
and is_privated  <=  #{is_privated}
and (
	title like concat('%', #{target}, '%') 
	or content like concat('%', #{target}, '%')
)

select * from board


		board_type = #{board_type}


	    and is_privated  <=  #{is_privated}


order by board_seq desc
limit #{offset}, #{count}

select * from board


		(title like concat('%', #{target}, '%') 
		or content like concat('%', #{target}, '%'))


	    and board_type = #{board_type}


	    and is_privated  <=  #{is_privated}


order by board_seq desc
